const FasamImc = require('./index');
const assert = require('assert');

describe('FasamImc', function() {
    describe('#construct(90, 1.60)', function() {
        it('should return 35.16', function() {
            const imc = new FasamImc(90, 1.60);
            const res = imc.calc(90, 1.60);
            assert.equal(res, 35.16);
        });
    });

    describe('#calc(80, 1.80)', function() {
        it('should return 24.69', function() {
            const imc = new FasamImc();
            const res = imc.calc(80, 1.80);
            assert.equal(res, 24.69);
        });
    });

    describe('#calc(90, 1.60) and #calc(120, 2.10) with the same object', function() {
        it('should return 35.16 and 27.21 with the same object', function() {
            const imc = new FasamImc();
            let res = imc.calc(90, 1.60);
            assert.equal(res, 35.16);
            res = imc.calc(120, 2.10);
            assert.equal(res, 27.21);
        });
    });

    describe('#calc("notanumber", 1.60)', function() {
        it('should throw error', function() {
            const imc = new FasamImc();
            assert.throws(() => imc.calc("notanumber", 1.60), Error);
        });
    });

});